mapper.update:
update member
set USER_ID = #{userId}
	,USER_PWD = #{userPwd}
	,USER_NAME = #{userName}
	,PHONE = #{phone}
	,POINT = #{point}
	,STATUS = #{status}
where user_no = #{userNo}

update role
set ROLE_TYPE = #{roleType}
where user_no = #{userNo}

mapper.insert:
	
        SELECT seq_me.nextval FROM dual
    
	insert into member
			(USER_NO
			,USER_ID
			,USER_PWD
			,USER_NAME
			,PHONE
			,POINT
			,ENROLLDATE
			,STATUS)
	values(
			#{userNo}
			,#{userId}
			,#{userPwd}
			,#{userName}
			,#{phone}
			,#{point}
			,SYSDATE
			,'Y')

mapper.select:
select * from( select USER_NO ,USER_ID ,USER_PWD ,USER_NAME ,PHONE ,POINT ,ENROLLDATE ,MODIFYDATE ,STATUS ,MAIN_COORD ,MAIN_LOCATION ,rownum as rn from( select m.USER_NO ,m.USER_ID ,m.USER_PWD ,m.USER_NAME ,m.PHONE ,m.POINT ,m.ENROLLDATE ,m.MODIFYDATE ,m.STATUS ,m.MAIN_COORD ,m.MAIN_LOCATION from member m
m.user_no = #{searchKeyword}
m.user_id like '%' || #{searchKeyword} ||'%'
m.user_name like '%' || #{searchKeyword} ||'%'
m.phone = #{searchKeyword}
AND not EXISTS ( SELECT 1 FROM role r WHERE r.USER_NO = m.USER_NO )
order by m.USER_NO ) where rownum <= #{endRow} ) where rn >= #{startRow}

