mapper.update:
update member
set USER_ID = #{userId}
	,USER_PWD = #{userPwd}
	,USER_NAME = #{userName}
	,PHONE = #{phone}
	,POINT = #{point}
	,STATUS = #{status}
where user_no = #{userNo}

update role
set ROLE_TYPE = #{roleType}
where user_no = #{userNo}

