mapper.select:
select * from( select USER_NO ,USER_ID ,USER_PWD ,USER_NAME ,PHONE ,POINT ,ENROLLDATE ,MODIFYDATE ,STATUS ,MAIN_COORD ,MAIN_LOCATION ,rownum as rn from( select USER_NO ,USER_ID ,USER_PWD ,USER_NAME ,PHONE ,POINT ,ENROLLDATE ,MODIFYDATE ,STATUS ,MAIN_COORD ,MAIN_LOCATION from member
(USER_NO LIKE '%' || #{searchKeyword} || '%' OR USER_ID LIKE '%' || #{searchKeyword} || '%' OR USER_NAME LIKE '%' || #{searchKeyword} || '%' OR PHONE LIKE '%' || #{searchKeyword} || '%')
order by USER_NO ) where rownum <= #{endRow} ) where rn >= #{startRow}

