mapper.select:
select count(*)
from PRODUCT_BOARD
where user_no = #{userNo}

	
	select *
	from(
		select PRODUCT_NO
				,CATEGORY_NAME
				,SCORE
				,USER_NO
				,PRICE
				,UPLOAD_DATE
				,PRODUCT_COUNT
				,PRODUCT_TITLE
				,PRODUCT_CONTENT
				,STATUS
				,rownum as rn
		from (select
				PRODUCT_NO
				,CATEGORY_NAME
				,SCORE
				,USER_NO
				,PRICE
				,UPLOAD_DATE
				,PRODUCT_COUNT
				,PRODUCT_TITLE
				,PRODUCT_CONTENT
				,STATUS
			from PRODUCT_BOARD pb
			join CATEGORY using (CATEGORY_NO)
			where user_no = #{userNo}
			
                
            	
                    and CATEGORY_NO = #{searchType1}
                    
                        
                            and PRODUCT_TITLE LIKE #{searchPattern}
                        
                        
                            and PRODUCT_CONTENT LIKE #{searchPattern}
                        
                        
                            and (PRODUCT_TITLE LIKE #{searchPattern} 
                                 OR PRODUCT_CONTENT LIKE #{searchPattern})
                        
                    
				
				
				
					
						
                            and PRODUCT_TITLE LIKE #{searchPattern}
						
						
                            and PRODUCT_CONTENT LIKE #{searchPattern}
						
						
                            and (PRODUCT_TITLE LIKE #{searchPattern} 
                                 OR PRODUCT_CONTENT LIKE #{searchPattern})
						
					
				
			
			order by UPLOAD_DATE desc
			)
		where rownum <= #{endRow}
		)
	where rn >= #{startRow}
