mapper.select:
	
		select count(*)
		from PRODUCT_BOARD
		where user_no = #{userNo}
		
        
			
				and CATEGORY_NO = #{searchType1}
				
					
						and PRODUCT_TITLE LIKE #{searchPattern}
					
					
						and PRODUCT_CONTENT LIKE #{searchPattern}
					
					
						and (PRODUCT_TITLE LIKE #{searchPattern} 
							OR PRODUCT_CONTENT LIKE #{searchPattern})
					
				
			
			
			
				
					
                         and PRODUCT_TITLE LIKE #{searchPattern}
					
					
                         and PRODUCT_CONTENT LIKE #{searchPattern}
					
					
                         and (PRODUCT_TITLE LIKE #{searchPattern} 
                              OR PRODUCT_CONTENT LIKE #{searchPattern})

	
		select *
		from(
			select t.*, rownum as rn
			from (select
					pb.PRODUCT_NO
					,c.CATEGORY_NAME
					,pb.PRICE
					,pb.UPLOAD_DATE
					,pb.PRODUCT_TITLE
					,pb.PRODUCT_CONTENT
					,pb.STATUS
					,count(r.REPLY_NO) as COUNT_REPLY
				from PRODUCT_BOARD pb
				left join CATEGORY c on (c.CATEGORY_NO=pb.CATEGORY_NO)
				left join REPLY r on(r.PRODUCT_NO = pb.PRODUCT_NO)
				where user_no = #{userNo}
				
	                
	            	
	                    and pb.CATEGORY_NO = #{searchType1}
	                    
	                        
	                            and pb.PRODUCT_TITLE LIKE #{searchPattern}
	                        
	                        
	                            and pb.PRODUCT_CONTENT LIKE #{searchPattern}
	                        
	                        
	                            and (pb.PRODUCT_TITLE LIKE #{searchPattern} 
	                                 OR pb.PRODUCT_CONTENT LIKE #{searchPattern})
	                        
	                    
					
					
					
						
							
	                            and pb.PRODUCT_TITLE LIKE #{searchPattern}
							
							
	                            and pb.PRODUCT_CONTENT LIKE #{searchPattern}
							
							
	                            and (pb.PRODUCT_TITLE LIKE #{searchPattern} 
	                                 OR pb.PRODUCT_CONTENT LIKE #{searchPattern})
							
						
					
				
				group by pb.PRODUCT_NO, c.CATEGORY_NAME, pb.PRICE,
                     pb.UPLOAD_DATE, pb.PRODUCT_TITLE, 
                     pb.PRODUCT_CONTENT, pb.STATUS
				order by UPLOAD_DATE desc
				) t
			where rownum <= #{endRow}
			)
		where rn >= #{startRow}

select count(*)
from REPLY
where user_no = #{userNo}

	and CATEGORY_NO = #{searchType1}

select * from( select REPLY_NO ,PRODUCT_NO ,PRODUCT_TITLE ,USER_NO ,REPLY_TEXT ,REPLY_DATE ,rownum as rn from (select p.REPLY_NO ,p.PRODUCT_NO ,.PRODUCT_TITLE ,p.USER_NO ,p.REPLY_TEXT ,p.REPLY_DATE from REPLY p left join PRODUCT_BOARD pb on (p.PRODUCT_NO=pb.PRODUCT_NO) where user_no = #{userNo}
and pb.CATEGORY_NO = #{searchType1}
order by REPLY_DATE desc ) where rownum <= #{endRow} ) where rn >= #{startRow}
