mapper.update:
UPDATE PRODUCT_BOARD  
SET SCORE = (
  SELECT ROUND(AVG(SCORE), 2)
  FROM RATING
  WHERE PRODUCT_NO = #{productNo}
)
WHERE PRODUCT_NO = #{productNo}

mapper.select:
SELECT COUNT(*)
FROM SCORE
WHERE USER_NO = #{userNo}

